import { EnvironmentUtil } from "../environmentUtil.js";
class FilePathDelimiter {
    static get PathDelimiter() {
        if (EnvironmentUtil.isWindows) {
            return FilePathDelimiter.PathsInWindow;
        }
        else if (EnvironmentUtil.isLinux) {
            return FilePathDelimiter.PathsInLinux;
        }
        return FilePathDelimiter.PathsDefault;
    }
}
////////////////////////////////////////////////
// @静态常量
////////////////////////////////////////////////
FilePathDelimiter.PathsDefault = "/";
FilePathDelimiter.PathsInWindow = "\\";
FilePathDelimiter.PathsInLinux = "/";
FilePathDelimiter.FileExtensionNameDelimiter = ".";
export { FilePathDelimiter };
